// src/components/ParallaxComponent.js
import React, { useEffect } from 'react';
import './ParallaxComponent.css';
import Navbar from './Navbar';

const ParallaxComponent = () => {
    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.pageYOffset;
            const parallaxElement = document.querySelector('.parallax-background');
            if (parallaxElement) {
                const zoomFactor = 1 + scrollPosition * 0.0005; // Adjust the zoom factor to your liking
                parallaxElement.style.transform = `scale(${zoomFactor})`;
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div>
            <Navbar />
            <div className="parallax-container">
                <div className="parallax-background"></div>
                <div className="content">
                    <h1 className="heading">Building New Age</h1>
                    <h2 className="sub-heading">Healthcare With AI</h2>
                </div>
            </div>
        </div>
    );
};

export default ParallaxComponent;
