// src/components/ParallaxComponent.js
import React, { useEffect } from 'react';
import './ParallaxComponent.css';
import Navbar from './Navbar';

const ParallaxComponent = () => {
    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.pageYOffset;
            const parallaxElement = document.querySelector('.parallax-background');
            if (parallaxElement) {
                const zoomFactor = 1 + scrollPosition * 0.0005; // Adjust the zoom factor to your liking
                parallaxElement.style.transform = `scale(${zoomFactor})`;
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className="parallax-container">
            <Navbar />
            <div className="parallax-background"></div>
            <div className="content">
                <h1 className='heading'>Parallax Zoom Effect</h1>
                {/* <p>Scroll down to see the effect.</p>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus lacinia odio vitae vestibulum vestibulum. Cras venenatis euismod malesuada.</p>
                <p>Scroll more...</p>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus lacinia odio vitae vestibulum vestibulum. Cras venenatis euismod malesuada.</p>
                <p>Keep scrolling...</p> */}
            </div>
        </div>
    );
};

export default ParallaxComponent;
