// src/components/ParallaxComponent.js
import React, { useEffect, useState } from 'react';
import './ParallaxComponent.css';
import Navbar from './Navbar';

const ParallaxComponent = () => {
    const [scrollPosition, setScrollPosition] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setScrollPosition(window.pageYOffset);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const zoomFactor = 1 + scrollPosition * 0.0005; // Adjust the zoom factor to your liking

    return (
        <div className="parallax-container">
            <Navbar />
            <div
                className="parallax-background"
                style={{ transform: `scale(${zoomFactor})` }}
            ></div>
            <div className="content">
                <h1 className="heading">Building New Age</h1>
                <h2 className="sub-heading">Healthcare With AI</h2>
            </div>
        </div>
    );
};

export default ParallaxComponent;
